sys_ux_client_script.script:
function handler({
    api,
    event,
    imports
}) {
    const {
        setState,
        emit
    } = api;

    const table = event.payload.table;
    const sysId = event.payload.sysId;
    const route = "record_request";

    if (table === "sc_req_item") {
        emit('NAV_ITEM_SELECTED', {
            route,
            fields: {
                table,
                sysId
            }
        });
    } else if(table == "incident"){
        emit('NAV_ITEM_SELECTED', {
            route,
            fields: {
                table: "incident",
                sysId
            }
        });
    }
    else{
        emit('NAV_ITEM_SELECTED', {
            route,
            fields: {
                table: "task",
                sysId
            }
        });
    }
}
